for (const item of $input.all()) {
  try {
    if (item.json.output && typeof item.json.output === "string") {
      let messageContent = item.json.output;

      // üëâ L√†m s·∫°ch n·ªôi dung ƒë·∫ßu v√†o:
      messageContent = messageContent.replace(/,\s*$/gm, "").replace(/"/g, "");

      // üëâ C·∫Øt b·ªè to√†n b·ªô ph·∫ßn n·∫±m tr∆∞·ªõc `**ID:` ho·∫∑c `**ID:**`
      const idStartIndex = messageContent.indexOf("**ID:");
      if (idStartIndex > -1) {
        messageContent = messageContent.slice(idStartIndex);
      }

      // üëâ Danh s√°ch c√°c regex ƒë·ªãnh d·∫°ng v√† √°nh x·∫° t∆∞∆°ng ·ª©ng
      const regexFormats = [
        {
          // ‚úÖ Tr∆∞·ªùng h·ª£p: **ID:** 36
          regex:
            /\*\*ID:\*\*\s*(\d+)[\s\S]*?\*\*Pain[_ ]Point:\*\*\s*([\s\S]*?)\n+\*\*Hook:\*\*\s*([\s\S]*?)\n+\*\*Content:\*\*\s*([\s\S]*?)\n+\*\*Comment[_ ]Content:\*\*\s*([\s\S]*?)\n+\*\*CTA:\*\*\s*([\s\S]*?)(?:\n|$)/i,
          fields: [
            "id",
            "pain_point",
            "hook",
            "content",
            "comment_content",
            "cta",
          ],
        },
        {
          // ‚úÖ Tr∆∞·ªùng h·ª£p: **ID: 36** (d·∫•u ":" n·∫±m trong ph·∫ßn **ID: 36**)
          regex:
            /\*\*ID:\s*(\d+)\*\*[\s\S]*?\*\*Pain[_ ]Point:\*\*\s*([\s\S]*?)\n+\*\*Hook:\*\*\s*([\s\S]*?)\n+\*\*Content:\*\*\s*([\s\S]*?)\n+\*\*Comment[_ ]Content:\*\*\s*([\s\S]*?)\n+\*\*CTA:\*\*\s*([\s\S]*?)(?:\n|$)/i,
          fields: [
            "id",
            "pain_point",
            "hook",
            "content",
            "comment_content",
            "cta",
          ],
        },
        {
          // ‚úÖ Tr∆∞·ªùng h·ª£p: Comment_Content ƒë·ª©ng tr∆∞·ªõc Hook
          regex:
            /\*\*ID:\*\*\s*(\d+)[\s\S]*?\*\*Pain Point:\*\*\s*([\s\S]*?)\n+\*\*Comment[_ ]Content:\*\*\s*([\s\S]*?)\n+\*\*Hook:\*\*\s*([\s\S]*?)\n+\*\*Content:\*\*\s*([\s\S]*?)\n+\*\*CTA:\*\*\s*([\s\S]*?)(?:\n|$)/i,
          fields: [
            "id",
            "pain_point",
            "comment_content",
            "hook",
            "content",
            "cta",
          ],
        },
        {
          // ‚úÖ D·∫°ng markdown kh√¥ng ƒë·∫ßy ƒë·ªß - ch·ªâ ID th∆∞·ªùng
          regex:
            /ID:\s*(\d+)[\s\S]*?Pain Point:\s*(.*?)\n+Hook:\s*(.*?)\n+Content:\s*([\s\S]*?)\n+Comment_Content:\s*([\s\S]*?)\n+CTA:\s*([\s\S]*?)(?:\n|$)/i,
          fields: [
            "id",
            "pain_point",
            "hook",
            "content",
            "comment_content",
            "cta",
          ],
        }
      ];

      let extractedData = {};
      for (const format of regexFormats) {
        const match = format.regex.exec(messageContent);
        if (match) {
          const post = {};
          format.fields.forEach((field, index) => {
            post[field] = match[index + 1]?.trim() || "";
          });
          extractedData["post"] = post;
          break;
        }
      }

      if (!extractedData.post) {
        throw new Error("No valid data extracted from input.");
      }

      item.json = {
        ...item.json,
        extracted_data: extractedData,
      };
    } else {
      item.json = {
        ...item.json,
        error: "No valid output found in input.",
      };
    }
  } catch (error) {
    item.json = {
      ...item.json,
      error: "Error processing content",
      details: error.message,
    };
  }
}

return $input.all();

