// Loop over input items
for (const item of $input.all()) {
  try {

    // Ki·ªÉm tra v√† x·ª≠ l√Ω extracted_data.post.content n·∫øu t·ªìn t·∫°i
    if (item.json.extracted_data && item.json.extracted_data.post && item.json.extracted_data.post.content) {
      item.json.extracted_data.post.content = item.json.extracted_data.post.content.replace(/\n/g, '\\n');
    }

    // Lu√¥n x·ª≠ l√Ω output ƒë·ªÉ ƒë·∫£m b·∫£o t√≠nh nh·∫•t qu√°n
    if (item.json.output) {
      item.json.output = item.json.output.replace(/\n/g, '\\n');
    }

    // N·∫øu kh√¥ng c√≥ c·∫£ output l·∫´n extracted_data.post.content, n√©m l·ªói
    if (!item.json.output && !(item.json.extracted_data && item.json.extracted_data.post && item.json.extracted_data.post.content)) {
      throw new Error("No valid content field found to process.");
    }
  } catch (error) {
    // Ghi l·ªói v√†o m·ªôt tr∆∞·ªùng ph√π h·ª£p n·∫øu x·ª≠ l√Ω th·∫•t b·∫°i
    item.json.error = "Error processing content: " + error.message;
  }
}

// Tr·∫£ v·ªÅ danh s√°ch c√°c item ƒë√£ ƒë∆∞·ª£c c·∫≠p nh·∫≠t
