// Loop over input items and create a single string combining titles and contents
const output = [];

for (const item of $input.all()) {
    const newItem = item.json;

    // T·∫°o chu·ªói t·ª´ t·∫•t c·∫£ c√°c c·∫∑p ti√™u ƒë·ªÅ - n·ªôi dung
    newItem.combinedString = Object.entries(newItem)
        .filter(([key, value]) => key !== "row_number") // B·ªè qua tr∆∞·ªùng kh√¥ng c·∫ßn thi·∫øt n·∫øu mu·ªën
        .map(([key, value]) => `${key}: ${value}`)
        .join(",");

    output.push(newItem);
}

return output;

for (const item of $input.all()) {
  try {
    if (item.json.output && typeof item.json.output === 'string') {
      let messageContent = item.json.output
        .replace(/,\s*$/gm, '') // X√≥a d·∫•u ph·∫©y cu·ªëi d√≤ng
        .replace(/"/g, '')      // X√≥a d·∫•u ngo·∫∑c k√©p
        .trim();

      // ‚úÖ H√†m x·ª≠ l√Ω c·∫£ 2 ki·ªÉu slide: subject-describe & - **Step X**
      function extractSlides(text) {
        const slides = [];

        // Ki·ªÉu 1: subject + describe
        const subjectDescribePattern = /\*\*subject:\*\*\s*(.*?)\n\*\*describe:\*\*\n([\s\S]*?)(?=\n\*\*subject:|\Z)/g;
        let match;
        while ((match = subjectDescribePattern.exec(text)) !== null) {
          const subject = match[1].trim();
          const describe = match[2].trim().split('\n')
            .map(line => line.replace(/^-/, '').trim())
            .filter(line => line.length > 0);
          slides.push({ subject, describe });
        }

        // Ki·ªÉu 2: - **Step X** + m√¥ t·∫£
        const stepPattern = /-\s\*\*(Step\s\d+\..*?)\*\*\n((?:\s{2,}-\s.*\n?)+)/g;
        let matchStep;
        while ((matchStep = stepPattern.exec(text)) !== null) {
          const subject = matchStep[1].trim();
          const describeRaw = matchStep[2];
          const describe = describeRaw.split('\n')
            .map(line => line.trim().replace(/^-\s/, ''))
            .filter(line => line.length > 0);
          slides.push({ subject, describe });
        }

        return slides;
      }

      // ‚úÖ H√†m tr√≠ch xu·∫•t c√°c field ch√≠nh
      function extractContent(data) {
        const getField = (label, fallback = '') => {
          const regex = new RegExp(`\\*\\*${label}:\\*\\*\\s*([\\s\\S]*?)(?=\\n\\*\\*|\\n---|\\n$|$)`, 'i');
          const match = data.match(regex);
          return (match && match[1].trim()) || fallback;
        };

        return {
          post: {
            id: getField('ID', 'Not specified'),
            pain_point: getField('Pain_Point', 'Not specified'),
            hook: getField('Hook', 'Not specified'),
            image_url: getField('Image_URL', ''),
            content: getField('Content', ''),
            slide: extractSlides(data),
            comment_content: getField('Comment_Content', ''),
            cta: getField('CTA', ''),
            status: getField('Status', 'Not specified')
          }
        };
      }

      const extractedData = extractContent(messageContent);

      if (!extractedData.post.id || extractedData.post.id === 'Not specified') {
        throw new Error("No valid ID extracted from input.");
      }

      item.json = {
        ...item.json,
        extracted_data: extractedData
      };
    } else {
      item.json = {
        ...item.json,
        error: "No valid output found in input."
      };
    }
  } catch (error) {
    item.json = {
      ...item.json,
      error: "Error processing content",
      details: error.message
    };
  }
}

return $input.all();

for (const item of $input.all()) {
  try {
    if (item.json.output && typeof item.json.output === 'string') {
      let messageContent = item.json.output;

      // Clean up the input: remove unwanted formatting but keep hashtags
      messageContent = messageContent
        .replace(/\*/g, '') // Ch·ªâ x√≥a * thay v√¨ c·∫£ # v√† *
        .replace(/,\s*$/gm, '') // X√≥a d·∫•u ph·∫©y th·ª´a cu·ªëi d√≤ng
        .replace(/"/g, '') // X√≥a d·∫•u ngo·∫∑c k√©p
        .trim(); // X√≥a kho·∫£ng tr·∫Øng th·ª´a ƒë·∫ßu/cu·ªëi

      function extractContent(data) {
        const results = {};
        const platformKey = "post";

        // More flexible regex patterns for each field
        const idRegex = /ID:\s*([A-Za-z0-9_]+)/i;
        const painPointRegex = /Pain_Point:\s*([\s\S]*?)(?=\n(?:Hook|Image_URL|Content|Comment_Content|CTA|Status|$))/i;
        const hookRegex = /Hook:\s*([\s\S]*?)(?=\n(?:Image_URL|Content|Comment_Content|CTA|Status|$))/i;
        const imageUrlRegex = /Image_URL:\s*([^\s]+)(?=\n|$)/i;
        const contentRegex = /Content:\s*([\s\S]*?)(?=\n(?:Comment_Content|CTA|Status|$))/i;
        const commentContentRegex = /Comment_Content:\s*([\s\S]*?)(?=\n(?:CTA|Status|$))/i;
        const ctaRegex = /CTA:\s*([\s\S]*?)(?=\n(?:Status|$))/i;

        // Extract each field with fallback values
        results[platformKey] = {
          id: (data.match(idRegex) || [, "Not specified"])[1].trim(),
          pain_point: (data.match(painPointRegex) || [, "Not specified"])[1]?.trim() || "Not specified",
          hook: (data.match(hookRegex) || [, "Not specified"])[1]?.trim() || "Not specified",
          image_url: (data.match(imageUrlRegex) || [, "Not specified"])[1]?.trim() || "Not specified",
          content: (data.match(contentRegex) || [, "Not specified"])[1]?.trim() || "Not specified",
          comment_content: (data.match(commentContentRegex) || [, ""])[1]?.trim() || "",
          cta: (data.match(ctaRegex) || [, ""])[1]?.trim() || ""
        };

        return results;
      }

      let extractedData = extractContent(messageContent);

      if (!extractedData.post || !extractedData.post.id || extractedData.post.id === "Not specified") {
        throw new Error("No valid ID extracted from input.");
      }

      item.json = {
        ...item.json,
        extracted_data: extractedData
      };
    } else {
      item.json = { ...item.json, error: "No valid output found in input." };
    }
  } catch (error) {
    item.json = {
      ...item.json,
      error: "Error processing content",
      details: error.message
    };
  }
}

return $input.all();

for (const item of $input.all()) {
  try {
    // X·ª≠ l√Ω extracted_data.post.content
    if (item.json.extracted_data?.post?.content) {
      item.json.extracted_data.post.content = item.json.extracted_data.post.content
        .replace(/["‚Äú‚Äù]/g, '')       // X√≥a m·ªçi d·∫•u ngo·∫∑c k√©p: " ‚Äú ‚Äù
        .replace(/\n/g, '\\n');      // Escape newline
    }

    // (T√πy ch·ªçn) X·ª≠ l√Ω output n·∫øu b·∫°n v·∫´n c·∫ßn
    if (item.json.output) {
      item.json.output = item.json.output
        .replace(/["‚Äú‚Äù]/g, '')       // X√≥a m·ªçi d·∫•u ngo·∫∑c k√©p
        .replace(/\n/g, '\\n');
    }

    // Ki·ªÉm tra cu·ªëi
