for (const item of $input.all()) {
  try {
    if (item.json.images_results && Array.isArray(item.json.images_results)) {
      const validExtensions = ['.jpg', '.jpeg', '.png', '.gif', '.webp'];
      const blockedDomains = [
        'squarespace-cdn.com',
        'smartinsights.com',
        'redlineminds.com',
        'pptinfographics.com',
        'slideshare.net'
      ];

      const imageResults = item.json.images_results
        .filter(image => {
          const url = image.original || '';
          const urlLower = url.toLowerCase();

          // Ki·ªÉm tra ƒë·ªãnh d·∫°ng ·∫£nh
          const hasValidExtension = validExtensions.some(ext => urlLower.endsWith(ext));

          // Ki·ªÉm tra domain c√≥ b·ªã ch·∫∑n kh√¥ng
          const isBlockedDomain = blockedDomains.some(domain => urlLower.includes(domain));

          // Ch·ªâ gi·ªØ l·∫°i ·∫£nh h·ª£p l·ªá v√† kh√¥ng thu·ªôc domain ch·∫∑n
          return hasValidExtension && !isBlockedDomain;
        })
        .map(image => ({
          title: image.title || "",
          source: image.source || "",
          link: image.link || "",
          original: image.original || ""
        }));

      return imageResults;
    } else {
      return [{ error: "No valid image results found in input." }];
    }
  } catch (error) {
    return [{ error: "Error processing image results", details: error.message }];
  }
}

