          }

          // CASE 4: subject:
          else if (line.toLowerCase().startsWith('subject:')) {
            if (currentSlide && currentSlide.subject && currentSlide.describe.length > 0) {
              slides.push(currentSlide);
            }
            currentSlide = {
              subject: line.replace(/subject:/i, '').replace(/"/g, '').trim(),
              describe: []
            };
            isDescribeBlock = true;
          }

          // CASE 5: **Step X. Title**
          else if (/^\*\*Step\s*\d+\./i.test(line)) {
            if (currentSlide && currentSlide.subject && currentSlide.describe.length > 0) {
              slides.push(currentSlide);
            }
            currentSlide = {
              subject: line.replace(/\*\*/g, '').replace(/"/g, '').trim(),
              describe: []
            };
            isDescribeBlock = true;
