      const lines = slideContent.split('\n').filter(line => line.trim() !== '');

      // Bi·∫øn t·∫°m ƒë·ªÉ l∆∞u tr·ªØ slide hi·ªán t·∫°i
      let currentSlide = null;
      let currentDescribe = [];

      // Duy·ªát qua t·ª´ng d√≤ng ƒë·ªÉ t√°ch subject v√† describe
      for (const line of lines) {
